mapper.select:
SELECT r_sdate, r_uid, i_seq, i_title, i_price, i_ccode FROM tbl_user_minfo JOIN tbl_minfo ON r_iseq = i_seq
AND r_sdate BETWEEN #{s_sdate} AND #{s_edate}
AND r_uid LIKE CONCAT('%', #{s_uid}, '%')
AND i_title LIKE CONCAT('%', #{s_ititle}, '%') AND i_ccode = #{s_ccode}
ORDER BY r_sdate ASC

select * from tbl_user_minfo
where r_iseq = #{seq} and
r_uid = #{id}

mapper.update:
UPDATE tbl_user_minfo
SET
r_icount = #{r_icount},
r_sdate = #{r_sdate},
r_edate = #{r_edate}
WHERE r_iseq = #{r_iseq}
